INSERT INTO `tbl_nearby` VALUES (14,0,'mahankal-temple','New Sauraha Tharu Cultural House','Mahankal Temple','<p>\r\n	The temple of Mahankal is situated near New Road in Kathmandu. Mahankal denotes someone who is beyond time and the Lord Mahankal is also known as Mahankaleshvar. The temple as well as the statue of the god is one of the most amazing examples of the Nepalese ancient heritage. Every day thousands of devotees come to worship the Lord in this temple. The area is also called Mahankal because of the presence of the temple.</p>\r\n',0,'',1,'','','',0,'2023-09-04 13:26:09','2024-09-09 22:04:53',1,0,_binary 'a:1:{i:0;s:15:\"nOD7f-tharu.jpg\";}','','','950 m','https://maps.app.goo.gl/3Me82TthkM15GjYz6'),(15,0,'santi-stupa','Chitwan Tharu Village','santi stupa','',0,'',1,'','','',0,'2023-09-04 13:26:55','2024-09-09 22:04:24',2,0,_binary 'a:1:{i:0;s:17:\"Bua9A-village.jpg\";}','','','2 km','https://maps.app.goo.gl/GsThTbeQKsBUZYQK6'),(16,0,'changu-narayan-temple','Elephant Breeding Center','Changu Narayan Temple','',0,'',1,'','','',0,'2023-09-04 13:27:35','2024-09-09 22:03:50',3,0,_binary 'a:1:{i:0;s:18:\"mpeSu-elephant.jpg\";}','','','3.6 km','https://maps.app.goo.gl/mR5o7HHPETR4gdZ36'),(17,0,'dhulikhel','Tharu Cultural Museum & Research Center','Dhulikhel','',0,'',1,'','','',0,'2023-09-04 13:28:02','2024-09-09 22:01:57',4,0,_binary 'a:1:{i:0;s:15:\"Mj6NH-tharu.jpg\";}','','','1.6 km','https://maps.app.goo.gl/LNM6WNGLAXCcmswh6'),(18,0,'bhaktapur','Chitwan National Park','Bhaktapur','<p>\r\n	testing</p>\r\n',0,'',1,'','','',0,'2023-09-04 13:28:33','2024-09-09 22:01:20',5,0,_binary 'a:1:{i:0;s:17:\"HFFeX-chitwan.jpg\";}','','','35.7 km','https://maps.app.goo.gl/ByJCCa1h192hEZyp6');
