INSERT INTO `tbl_offers` VALUES (13,'canoeing','Canoeing','','8OXLC-Canoeing.jpg',0,0,'',0,0,0,'Canoeing along the Rapti River in Chitwan National Park offers a serene experience amidst Nepal\'s lush wilderness. Gliding through tranquil waters, surrounded by dense foliage and diverse wildlife, creates a harmonious blend of relaxation and adventure.','',1,'2024-09-04 11:30:40',1,0,'2024-09-30','2024-09-01'),(12,'cultural-dance','Cultural Dance','','tdhga-culturedance.jpg',0,0,'',0,0,0,'Cultural dance in Chitwan features vibrant Tharu community performances, showcasing their rich heritage and traditions with lively music and colorful costumes. These entertaining shows educate tourists about Tharu culture, making them a must-see for visit','',1,'2024-09-04 11:30:24',2,0,'2024-09-30','2024-09-01'),(11,'jungle-safari','Jungle Safari','','KzanG-jeepsafari.jpg',0,0,'',0,0,0,'Chitwan Jungle Safari offers immersive activities like jeep safaris, elephant rides, canoeing, and jungle walks in Chitwan National Park, where visitors can observe wildlife such as the one-horned rhinoceros, Royal Bengal tiger, and numerous bird species.','',1,'2024-09-04 11:29:19',3,0,'2024-09-30','2024-09-01');
